'use strict';

class ActionsManager {
  constructor(client) {
    this.client = client;

    this.register(require('discord.js/src/client/actions/MessageCreate'));
    this.register(require('discord.js/src/client/actions/MessageDelete'));
    this.register(require('discord.js/src/client/actions/MessageDeleteBulk'));
    this.register(require('discord.js/src/client/actions/MessageUpdate'));
    this.register(require('discord.js/src/client/actions/MessageReactionAdd'));
    this.register(require('discord.js/src/client/actions/MessageReactionRemove'));
    this.register(require('discord.js/src/client/actions/MessageReactionRemoveAll'));
    this.register(require('discord.js/src/client/actions/MessageReactionRemoveEmoji'));
    this.register(require('discord.js/src/client/actions/ChannelCreate'));
    this.register(require('discord.js/src/client/actions/ChannelDelete'));
    this.register(require('discord.js/src/client/actions/ChannelUpdate'));
    this.register(require('discord.js/src/client/actions/GuildDelete'));
    this.register(require('discord.js/src/client/actions/GuildUpdate'));
    this.register(require('discord.js/src/client/actions/InviteCreate'));
    this.register(require('discord.js/src/client/actions/InviteDelete'));
    this.register(require('discord.js/src/client/actions/GuildMemberRemove'));
    this.register(require('discord.js/src/client/actions/GuildMemberUpdate'));
    this.register(require('discord.js/src/client/actions/GuildBanRemove'));
    this.register(require('discord.js/src/client/actions/GuildRoleCreate'));
    this.register(require('discord.js/src/client/actions/GuildRoleDelete'));
    this.register(require('discord.js/src/client/actions/GuildRoleUpdate'));
    this.register(require('discord.js/src/client/actions/PresenceUpdate'));
    this.register(require('discord.js/src/client/actions/UserUpdate'));
    this.register(require('discord.js/src/client/actions/VoiceStateUpdate'));
    this.register(require('discord.js/src/client/actions/GuildEmojiCreate'));
    this.register(require('discord.js/src/client/actions/GuildEmojiDelete'));
    this.register(require('discord.js/src/client/actions/GuildEmojiUpdate'));
    this.register(require('discord.js/src/client/actions/GuildEmojisUpdate'));
    this.register(require('discord.js/src/client/actions/GuildRolesPositionUpdate'));
    this.register(require('discord.js/src/client/actions/GuildChannelsPositionUpdate'));
    this.register(require('discord.js/src/client/actions/GuildIntegrationsUpdate'));
    this.register(require('discord.js/src/client/actions/WebhooksUpdate'));
    this.register(require('discord.js/src/client/actions/TypingStart'));
  }

  register(Action) {
    this[Action.name.replace(/Action$/, '')] = new Action(this.client);
  }
}

module.exports = ActionsManager;
